import React from 'react'
import './gameboard.css'

export default function Gameboard() {
   let boxArr=[]
    let row = 10
    let coloum = 10
  let  addOrEven=0
    for(let boardCell=(row*coloum); boardCell>0;boardCell-=row){
        if((addOrEven++)%2==0){
            for(let i=boardCell;i>=(boardCell-(coloum-1));i--){
                boxArr.push({
                    id:`boxId${i}`,
                    className:`cell${i}`,
                    cellNUM:i,
                    splCel:''
                }
                )
            }
    }
    else{
        for(let j=(boardCell-(coloum-1));j<=boardCell;j++){
            (boxArr.push({
                id:`boxId${j}`,
                className:`cell${j}`,
                cellNUM:j,
                splCel:'',
                
            }))
        }
    }
    }
    console.log(boxArr)
    
    function BoardProp(prop) {
        return (<div className="propes">
            <h1>{prop.id}</h1>
            <h1>{prop.className}</h1>
            <h1>{prop.cellNUM}</h1>
            <h1>{prop.splCel}</h1>

            
        </div>)
    }
    return (

        <div>
            {
                boxArr.map((ele)=>{
                    <BoardProp name = {ele.id,ele.className,ele.cellNUM,ele.s} />
                })
            }
            
        </div>
  )
}
