let boxArr=[]
let row = 10
let coloum = 10
let  addOrEven=0
for(let boardCell=(row*coloum); boardCell>0;boardCell-=row){
    if((addOrEven++)%2===0){
        for(let i=boardCell;i>=(boardCell-(coloum-1));i--){
            boxArr.push({
                id:`boxId${i}`,
                className:`boxs cell${i}`,
                cellNUM:i,
                splCel:''
            }
            )
        }
}
else{
    for(let j=(boardCell-(coloum-1));j<=boardCell;j++){
        (boxArr.push({
            id:`boxId${j}`,
            className:` boxs cell${j}`,
            cellNUM:j,
            splCel:'',
            
        }))
    }
}
}
