import React, { useState } from 'react'
import boxArr from '../BOXARRAY/boxarry'
import './dice.css'
import Props from '../Props'




let refClickCount =0
export default function Dice() {
    const[diceRoll,setDiceRoll]=useState("start")
    const[player1Position,setPlayer1Position]=useState(1)
    let player1Place = 1
    let player2Place = 1


    function dicerollfunction(){
        let diceRandom = (Math.floor(Math.random() * 6) + 1)
        if ((player1Place+diceRandom) <= 100 && (player2Place +diceRandom)<= 100 ) {
        refClickCount+=1  
        if(refClickCount%2===1){
            console.log("player1jjj", diceRandom,refClickCount)
            setDiceRoll("PLAYER-1ðŸ’ ROLL DICE")
           boxArr.forEach((ids)=>{
            // console.log( ids.id)
            if(ids.id===(player1Position+diceRandom)){
              console.log(player1Position+diceRandom)
              console.log(boxArr[player1Place].players.Player1)
              console.log("hello")
              ids.players.player1place='in'
                       }
          setPlayer1Position(player1Position+diceRandom)
           })
          //  console.log(playerdice,"id")

            
        }
        else if(refClickCount%2===0){
            console.log("player2", diceRandom,refClickCount)
            return setDiceRoll("PLAYER-2ðŸ”˜ROLL DICE")
        }

    }}
    
    
  return (
    <div className='playersRollDice'>
    <h1>{diceRoll}</h1>
    <button id='dicerollbutton' onClick={dicerollfunction}>click</button>
    <div className='boardsgrid'>
            {
                boxArr.map(ele=><Props array={boxArr.players} key={ele.id} id = {ele.id} className = {ele.className} cellNUM ={ele.cellNUM} />)
                // boxArr.map(ele=><Props id={ele.id}/>)
}
            
        </div>
    </div>
  )
}
