import React from 'react'
import './gameboard.css'

export default function Gameboard() {
   let boxArr=[]
    let row = 10
    let coloum = 10
  let  addOrEven=0
    for(let boardCell=(row*coloum); boardCell>0;boardCell-=row){
        if((addOrEven++)%2===0){
            for(let i=boardCell;i>=(boardCell-(coloum-1));i--){
                boxArr.push({
                    id:`boxId${i}`,
                    className:`box cell${i}`,
                    cellNUM:i,
                    splCel:''
                }
                )
            }
    }
    else{
        for(let j=(boardCell-(coloum-1));j<=boardCell;j++){
            (boxArr.push({
                id:`boxId${j}`,
                className:` boxcell${j}`,
                cellNUM:j,
                splCel:'',
                
            }))
        }
    }
    }
    console.log(boxArr)
    
    function BoardProp(props) {
        return (<div className="propes">
            {props.id}
            {props.className}
            <h1>{props.cellNUM}</h1>
            {props.splCel}

            
        </div>)
    }
    return (

        <div className='boardsgrid'>
            {
                boxArr.map((ele)=>{
                    return <BoardProp id = {ele.id} className = {ele.className} cellNUM ={ele.cellNUM} />
                })
            }
            
        </div>
  )
}
