import React from 'react'
import './gameboard.css'
import boxArr from '../BOXARRAY/boxarry'

export default function Gameboard() {
  
    
    function BoardProp(props) {
        let visible=props.array
        if(array.players.player1place==='in'){
            visible=<div>P1</div>
        }
        return (
        <div className="propes">
          <div className={props.className} id= {props.id}>
            {visible}
            <h1 >{props.cellNUM}</h1>
            {props.splCel}

            </div>
        </div>
        )
    }
    return (

        <div className='boardsgrid'>
            {
                boxArr.map((ele)=>{
                    return <BoardProp id = {ele.id} className = {ele.className} cellNUM ={ele.cellNUM} />
                })
            }
            
        </div>
  )
}
