import React, { useState } from 'react'
let refClickCount =0
export default function Dice() {
    const[diceRoll,setDiceRoll]=useState("start")
    let player1Place = 1
    let player2Place = 1
    function dicerollfunction(){
        let diceRandom = (Math.floor(Math.random() * 6) + 1)
        if ((player1Place+diceRandom) <= 100 && (player2Place +rand)<= 100 ) {
        refClickCount+=1
        
        
        if(refClickCount%2===1){
            console.log("player1", diceRandom,refClickCount)
            return setDiceRoll("player11111")

            
        }
        else if(refClickCount%2===0){
            console.log("player2", diceRandom,refClickCount)
            return setDiceRoll("player2222")
        }

    }}
    
    
  return (
    <div>
    <h1>{diceRoll}</h1>
    <button onClick={dicerollfunction}>click</button>
    </div>
  )
}
